    },
    created(){
        var parks = []
        for(var i=0;i<this.parks.length;i++){
            parks.push(this.parks[i].park)
        }
        this.parks = parks;

        this.park = localStorage.getItem('defaultPark');
        this.park = this.park ? this.park : '';

        this.uploads = localStorage.getItem('uploads');
        this.uploads = this.uploads ? JSON.parse(this.uploads) : [];
        console.log("uploads:", this.uploads)
    },
    methods:{
        ParkSelected: function(){
            localStorage.setItem('defaultPark', this.park);
        },
        StartClicked: function(){
            this.$router.push('/counter/' + this.park)
        },
        EditClicked: function(index){
            this.$router.push('/report/' + index);
        },
        SendData: function(){
            var email = localStorage.getItem('defaultEmail');
            if(email == null || email == ''){
                email = '';
            }
            this.selectedUploads = []
            for(let i=0;i<this.uploads.length;i++){
                if(this.uploads[i].selected){
                    this.selectedUploads.push(this.uploads[i]);
                }
            }
            if(!this.selectedUploads.length){
                return;
            }
            var uploads = '';

            for(var i=0; i<this.selectedUploads.length;i++){
                var upload = '';
                // Add info labels
                upload += "Staff Name; Trail; Route; Collection Type; Weather; Notes; Visitation; Status; Conditions; Start; End; Date; Park; %0D%0A";

                // Check regular information
                var info = ''
                for(const [key, value] of Object.entries(this.selectedUploads[i].session)){
                    let k = `${key}`; let v = `${value}`
                    console.log(k + " -> " + v);
                    info += v;
                    info += ';'
                }
                upload += info + '%0D%0A'

                // Add count labels
                upload += "On Foot; Biking; Ebiking; Horseback; Dog Off Leash; Count Start; Count End;%0D%0A"

                // Check counts
                console.log("-->", this.selectedUploads.counts)
                for(var j=0; j<this.selectedUploads[i].counts.length;j++){
                    let objEntry = ''
                    var obj = this.selectedUploads[i].counts[j]

                    for(const [key, value] of Object.entries(obj)){
                        let k = `${key}`; let v = `${value}`
                        console.log(k + " -> " + v);
                        objEntry += v;
                        objEntry += ';'
                    }
                    upload += objEntry + '%0D%0A'
                    console.log("final upload:", upload)
                }

                uploads += upload;
            }
            // Email function
            window.location.href="mailto:" + email + "?subject=Data Submission" +"&body=" + uploads;
        },
        DeleteData: function(){
            var deleteIndexArray = []
            for(let i=0;i<this.uploads.length;i++){
                if(this.uploads[i].selected){
                    deleteIndexArray.push(i)
                }
            }
            for (var i = deleteIndexArray.length -1; i >= 0; i--){
                this.uploads.splice(deleteIndexArray[i],1);
            }
            localStorage.setItem('uploads', JSON.stringify(this.uploads));
        }
    }
}
